import React from 'react';
import './styles/Project.css';
import { FaTrash, FaEye, FaRedo } from 'react-icons/fa';
import { Link } from "react-router-dom";

const Project = ({ project, onDelete, onToggle }) => {
    return (
        <div>
            <div className="project-component" onDoubleClick={() => onToggle(project.id)}>
                    <input type="text" className="project-title" value={project.title}></input><br/>
                    <textarea className="project-description" value={project.description}></textarea>
                    <Link to={`/projectpage/${project.id}`} className="view-icon"><FaEye/></Link>
                    <FaRedo className="update-icon"/>
                    <FaTrash className="delete-icon" onClick={() => onDelete(project.id)}/>
            </div>
        </div>
    )
}

export default Project
