import React from 'react'
import './styles/Projects.css';
import Project from './Project';
import ProjectTotal from './ProjectTotal';

const Projects = ({ projects, onDelete, updateProject }) => {

    const [edit, setEdit] = useState({
        id: null,
        value: ''
    });

    const submitUpdate = value => {
        updateProject(edit.id, value);
        setEdit({
            id: null,
            value: ''
        });
    };

    if (edit.id) {
        return <Project edit={edit} onSubmit={submitUpdate} />
    }

    return (
        <div>
            {projects.length > 0 ? <p className="list-title">Projects</p> : <p className="no-projects-msg">No projects yet</p>}
            {projects.map((project, index) => (<Project key={index} project={project} onDelete={onDelete} onClick={() => setEdit({id: project.id, value: project.title})}/>))}
            <ProjectTotal />
        </div>
    )
}

export default Projects
