import React, { useState, useEffect }from 'react';
import './styles/Project.css';
import { FaTrash, FaEye, FaRedo} from 'react-icons/fa';
import { Link } from "react-router-dom";
import EdiText from 'react-editext';


const Project = ({ project, onDelete}) => {
    const [title, setTitle] = useLocalStorage("")
    const [description, setDescription] = useLocalStorage("")

    const handleSave = (val) => {
        console.log('Edited value ->', val);
        setTitle(val);
    } 
    
    return (
        <div>
            <div className="project-component">
                    <EdiText 
                    type="text" 
                    className="project-title" 
                    value={title} 
                    onSave={handleSave} 
                    onChange={(e) => setTitle(e.target.title)}
                    showButtonsOnHover
                    submitOnUnfocus
                    cancelOnUnfocus
                    />
                    <br/>
                    <EdiText 
                    type="textarea" 
                    className="project-description" 
                    value={description} 
                    onSave={handleEdit} 
                    onChange={(e) => setDescription(e.target.description)}
                    showButtonsOnHover
                    submitOnUnfocus
                    cancelOnUnfocus
                    />
                    <Link to={`/projectpage/${project.id}`} className="view-icon"><FaEye/></Link>
                    <FaTrash className="delete-icon" onClick={() => onDelete(project.id)}/>
            </div>
        </div>
    )
}

  
export default Project
