import React, { useState }from 'react';
import './styles/Project.css';
import { FaTrash, FaEye, FaRedo} from 'react-icons/fa';
import { Link } from "react-router-dom";
import EdiText from 'react-editext';


const Project = ({ project, onDelete}) => {
    const [value, setValue] = useLocalStorage("value", "edit")
    const [description, setDescription] = useLocalStorage("value", "edit")

    const handleSave = (val) => {
        console.log('Edited value ->', val);
        setValue(val);
    } 

    return (
        <div>
            <div className="project-component">
                    <EdiText 
                    type="text" 
                    className="project-title" 
                    value={value} 
                    onSave={handleSave} 
                    onChange={(e) => setValue(e.target.value)}
                    showButtonsOnHover
                    submitOnUnfocus
                    cancelOnUnfocus
                    />
                    <br/>
                    <EdiText 
                    type="textarea" 
                    className="project-description" 
                    value={value} 
                    onSave={handleSave} 
                    onChange={(e) => setValue(e.target.value)}
                    showButtonsOnHover
                    submitOnUnfocus
                    cancelOnUnfocus
                    />
                    <Link to={`/projectpage/${project.id}`} className="view-icon"><FaEye/></Link>
                    <FaTrash className="delete-icon" onClick={() => onDelete(project.id)}/>
            </div>
        </div>
    )
}

// Hook
function useLocalStorage(key, initialValue) {
    // State to store our value
    // Pass initial state function to useState so logic is only executed once
    const [storedValue, setStoredValue] = useState(() => {
      try {
        // Get from local storage by key
        const item = window.localStorage.getItem(key);
        // Parse stored json or if none return initialValue
        return item ? JSON.parse(item) : initialValue;
      } catch (error) {
        // If error also return initialValue
        console.log(error);
        return initialValue;
      }
    });
    // Return a wrapped version of useState's setter function that ...
    // ... persists the new value to localStorage.
    const setValue = (value) => {
      try {
        // Allow value to be a function so we have same API as useState
        const valueToStore =
          value instanceof Function ? value(storedValue) : value;
        // Save state
        setStoredValue(valueToStore);
        // Save to local storage
        window.localStorage.setItem(key, JSON.stringify(valueToStore));
      } catch (error) {
        // A more advanced implementation would handle the error case
        console.log(error);
      }
    };
    return [storedValue, setValue];
  }

export default Project
